import React, { useState, useEffect } from "react";
import {
  Container,
  Grid,
  Modal,
  Typography,
  Tooltip,
  Zoom,
  Paper,
  Button,
} from "@material-ui/core";

import "./App.css";
import PlaylistLoader from "./PlaylistLoader";

function App() {
  const [videos, setVideos] = useState();
  const [curVid, setCurVid] = useState();

  const [curHover, setCurHover] = useState(2); //For rollover UX

  //When mounted, load playlists -> then add to state: setVideos
  useEffect(() => {
    PlaylistLoader((playlist) => {
      //console.log(playlist);
      setVideos(playlist);
    });
  }, []);

  return (
    <Container className="App" maxWidth="xl">
      <Typography variant="h4">Video playlist</Typography>
      <Grid container spacing={3} direction="row">
        {videos &&
          videos.map((v, i) => (
            <Grid item>
              <Tooltip
                key={i}
                title={v.title}
                placement="top"
                arrow
                TransitionComponent={Zoom}
                enterDelay={300}
              >
                <Paper
                  style={{ padding: 2, paddingBottom: 0 }}
                  onMouseEnter={() => setCurHover(i)}
                  onMouseLeave={() => setCurHover()}
                  elevation={curHover === i ? 5 : 1}
                  style={{ opacity: curHover === i ? 1 : 0.95 }}
                >
                  <img
                    src={v.image}
                    className="poster"
                    onClick={() => setCurVid(i)}
                  />
                </Paper>
              </Tooltip>
            </Grid>
          ))}
      </Grid>
      <Modal
        open={curVid > -1}
        onOpen={() => console.log("opemn")}
        onClose={() => console.log("close")}
      >
        <div className="popover">
          <div className="vidcontainer">
            <Button className="closeBtn" onClick={() => setCurVid()}>
              x
            </Button>
            {curVid > -1 && (
              <video
                src={videos[curVid].sources[6].file}
                className="vid"
                controls
                autoplay
                onLoadedData={(e) => e.target.play()}
              />
            )}
          </div>
        </div>
      </Modal>
    </Container>
  );
}

export default App;
