import React, { useState, useEffect } from "react";
import {
  Container,
  Grid,
  Modal,
  Typography,
  Tooltip,
  Zoom,
} from "@material-ui/core";
import "./App.css";
import DataSource from "./DataSource";

function App() {
  const dataSource: DataSource = new DataSource();
  const [videos, setVideos] = useState([]);
  const [curVid, setCurVid] = useState();

  useEffect(() => {
    dataSource.loadVideos((playlist) => {
      console.log(playlist);
      setVideos(playlist);
    });
  }, []);

  const runVid = (i) => {
    setCurVid(videos[i].sources[6].file);
  };

  return (
    <Container className="App" maxWidth="xl">
      <Typography variant="h4">Video playlist</Typography>
      <Grid container spacing={3} direction="row">
        {videos.map((v, i) => (
          <Grid item>
            <Tooltip
              title={v.title}
              placement="bottom"
              // arrow
              TransitionComponent={Zoom}
              enterDelay={300}
            >
              <div>
                <img
                  src={v.image}
                  className="poster"
                  onClick={() => runVid(i)}
                />
              </div>
            </Tooltip>
          </Grid>
        ))}
      </Grid>
      <Modal
        open={curVid}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        onOpen={() => console.log("opemn")}
        onClose={() => console.log("close")}
        onClick={() => setCurVid()}
      >
        <div className="popover">
          <div className="vidcontainer">
            <button className="closeBtn" onClick={() => setCurVid()}>
              x
            </button>
            <video
              src={curVid}
              className="vid"
              controls
              autoplay
              onLoadedData={(e) => e.target.play()}
              onClick={() => setCurVid()}
            />
          </div>
        </div>
      </Modal>
    </Container>
  );
}

export default App;
